mapper.select:
SELECT e.*, d.name AS deptName
FROM emp e
LEFT JOIN dept d ON e.dept_id = d.id


        AND e.name LIKE CONCAT('%', #{name}, '%')


        AND e.gender = #{gender}


        AND e.entry_date >= #{entryStartDate}


        AND e.entry_date <= #{entryEndDate}


ORDER BY e.update_time DESC

mapper.insert:
INSERT INTO emp (
    username, password, name, gender, phone, job, salary, image,
    entry_date, dept_id, create_time, update_time
) VALUES (
    #{username}, #{password}, #{name}, #{gender}, #{phone}, #{job}, #{salary}, #{image},
    #{entryDate}, #{deptId}, #{createDate}, #{updateDate}
)
